// Swiper JS
document.write('<script src="https://cdn.jsdelivr.net/npm/swiper@11/swiper-element-bundle.min.js"><\/script>');

const swiper = new Swiper('.swiper-container', {
    pagination: {
        el: '.swiper-pagination',
        clickable: true,
    },
    navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
    },
    spaceBetween: 10,
    centeredSlides: true,
    autoplay: {
        delay: 20000, // 20 segundos entre cada slide
        disableOnInteraction: false,
    },
    speed: 9000, // Velocidade da transição ajustada para 3 segundos
    effect: 'fade-in', // Adiciona efeito de fade para uma transição mais suave
});

function toggleDropdown() {
    var dropdown = document.getElementById("imoveisDropdown");
    if (dropdown.style.display === "block") {
        dropdown.style.display = "none";
    } else {
        dropdown.style.display = "block";
    }
}

document.querySelector('.hamburger').addEventListener('click', function () {
    document.querySelector('.mobile-nav').classList.toggle('active');
    document.querySelector('.hamburger').classList.toggle('active');
});

document.querySelectorAll('.mobile-nav a').forEach(link => {
    link.addEventListener('click', function () {
        const mobileNav = document.querySelector('.mobile-nav');
        const hamburger = document.querySelector('.hamburger');
        mobileNav.classList.remove('active');
        hamburger.classList.remove('active');
    });
});

document.addEventListener('DOMContentLoaded', function () {
    var i = 0;
    var tag = document.getElementById("text");
    var html = tag.innerHTML;
    var speed = 80;

    function typeWriter() {
        if (i < html.length) {
            tag.innerHTML = html.slice(0, i + 1); // Exibe o texto de 0 a i + 1
            i++;
            setTimeout(typeWriter, speed);        // Continua o loop
        } else {
            setTimeout(typeWriter, speed * 10);   // Pausa antes de recomeçar
        }
    }

    typeWriter();  // Inicia a função
});
